      gateway,
      // Disable subscriptions (not currently supported with ApolloGateway)
      subscriptions: false,
    });

    server.listen({ port: PORT}).then(({ url }) => {
      console.log(`ðŸš€ Server ready at ${url}`);
    });
  })();

